// Generate Dockerfiles for targets
function generate_dockerfiles() {
    const config_path = path.join(process.cwd(), 'omni.config.json');
    if (!fs.existsSync(config_path)) {
        print("Error: omni.config.json not found");
        return;
    }
    
    const config = JSON.parse(fs.readFileSync(config_path, 'utf-8'));
    
    if (!config.docker) {
        print("No 'docker' section in omni.config.json");
        return;
    }
    
    for (const [target_name, docker_config] of Object.entries(config.docker)) {
        const target = config.targets?.[target_name];
        if (!target) continue;
        
        const is_python = target.format === 'python';
        const output_dir = target.output;
        const port = docker_config.port || 3000;
        const base = docker_config.base || (is_python ? 'python:3.11-slim' : 'node:18-alpine');
        
        let dockerfile;
        if (is_python) {
            dockerfile = `# Generated by Omni Compiler
FROM ${base}
WORKDIR /app
COPY requirements.txt ./
RUN pip install --no-cache-dir -r requirements.txt || true
COPY ${output_dir}/ ./
EXPOSE ${port}
CMD ["python", "server.py"]
`;
        } else {
            dockerfile = `# Generated by Omni Compiler
FROM ${base}
WORKDIR /app
COPY package*.json ./
RUN npm install --production || true
COPY ${output_dir}/ ./
EXPOSE ${port}
CMD ["node", "server.js"]
`;
        }
        
        const dockerfile_path = path.join(output_dir, 'Dockerfile');
        if (!fs.existsSync(output_dir)) {
            fs.mkdirSync(output_dir, { recursive: true });
        }
        fs.writeFileSync(dockerfile_path, dockerfile);
        print("Generated: " + dockerfile_path);
    }
    
    print("Dockerfiles generated!");
}
// Generate SQL migrations from @entity structs
function generate_migrations() {
    const config_path = path.join(process.cwd(), 'omni.config.json');
    if (!fs.existsSync(config_path)) {
        print("Error: omni.config.json not found");
        return;
    }
    
    // Get dialect from command line: --dialect postgres
    let dialect = 'sqlite';
    const dialectIdx = process.argv.indexOf('--dialect');
    if (dialectIdx !== -1 && process.argv[dialectIdx + 1]) {
        dialect = process.argv[dialectIdx + 1];
    }
    
    const config = JSON.parse(fs.readFileSync(config_path, 'utf-8'));
    const shared_dirs = config.shared || [];
    const primitives = ['i64', 'i32', 'f64', 'f32', 'string', 'bool'];
    
    // Type mapping per dialect
    const typeMaps = {
        sqlite: { 'i64': 'INTEGER', 'i32': 'INTEGER', 'f64': 'REAL', 'f32': 'REAL', 'string': 'TEXT', 'bool': 'INTEGER' },
        postgres: { 'i64': 'BIGINT', 'i32': 'INTEGER', 'f64': 'DOUBLE PRECISION', 'f32': 'REAL', 'string': 'VARCHAR(255)', 'bool': 'BOOLEAN' },
        mysql: { 'i64': 'BIGINT', 'i32': 'INT', 'f64': 'DOUBLE', 'f32': 'FLOAT', 'string': 'VARCHAR(255)', 'bool': 'TINYINT(1)' }
    };
    const typeMap = typeMaps[dialect] || typeMaps.sqlite;
    
    let entities = []; // { name, table, storage, fields, dependencies }
    
    // Scan all shared directories for @entity structs
    for (const dir of shared_dirs) {
        const full_dir = path.join(process.cwd(), dir);
        if (!fs.existsSync(full_dir)) continue;
        
        const files = fs.readdirSync(full_dir).filter(f => f.endsWith('.omni'));
        for (const file of files) {
            const content = fs.readFileSync(path.join(full_dir, file), 'utf-8');
            
            const l = new_lexer(content);
            const p = new_parser(l);
            const program = Parser_parse_program(p);
            
            for (const stmt of program.statements) {
                if (stmt.kind === NODE_STRUCT && stmt.attributes) {
                    for (const attr of stmt.attributes) {
                        if (attr.name === 'entity') {
                            const storage = attr.params.storage || 'main_db';
                            const table = stmt.name;
                            
                            // Detect dependencies (FK references)
                            const dependencies = [];
                            for (const field of stmt.fields) {
                                if (!primitives.includes(field.typename) && field.name !== 'id') {
                                    dependencies.push(field.typename);
                                }
                            }
                            
                            entities.push({ name: stmt.name, table, storage, fields: stmt.fields, dependencies });
                        }
                    }
                }
            }
        }
    }
    
    if (entities.length === 0) {
        print("No @entity structs found in shared directories.");
        return;
    }
    
    // Topological sort for dependency ordering
    const sorted = [];
    const visited = new Set();
    const temp = new Set();
    
    function visit(entity) {
        if (temp.has(entity.name)) {
            print("Warning: Circular dependency detected involving " + entity.name);
            return;
        }
        if (visited.has(entity.name)) return;
        
        temp.add(entity.name);
        for (const dep of entity.dependencies) {
            const depEntity = entities.find(e => e.name === dep);
            if (depEntity) visit(depEntity);
        }
        temp.delete(entity.name);
        visited.add(entity.name);
        sorted.push(entity);
    }
    
    for (const entity of entities) visit(entity);
    
    // Generate SQL
    const migrations_dir = path.join(process.cwd(), 'dist', 'migrations');
    if (!fs.existsSync(migrations_dir)) {
        fs.mkdirSync(migrations_dir, { recursive: true });
    }
    
    let sql = '-- Generated by Omni Compiler (dialect: ' + dialect + ')\n\n';
    
    for (const entity of sorted) {
        let columns = [];
        let fks = [];
        
        for (const field of entity.fields) {
            if (field.name === 'id') {
                if (dialect === 'postgres') {
                    columns.push('    id SERIAL PRIMARY KEY');
                } else if (dialect === 'mysql') {
                    columns.push('    id BIGINT AUTO_INCREMENT PRIMARY KEY');
                } else {
                    columns.push('    id INTEGER PRIMARY KEY AUTOINCREMENT');
                }
            } else if (!primitives.includes(field.typename)) {
                // Foreign key
                columns.push('    ' + field.name + ' INTEGER');
                fks.push('    FOREIGN KEY (' + field.name + ') REFERENCES ' + field.typename + '(id)');
            } else {
                const sqlType = typeMap[field.typename] || 'TEXT';
                columns.push('    ' + field.name + ' ' + sqlType);
            }
        }
        
        const allCols = columns.concat(fks);
        sql += '-- Table: ' + entity.table + '\n';
        sql += 'CREATE TABLE IF NOT EXISTS ' + entity.table + ' (\n';
        sql += allCols.join(',\n');
        sql += '\n);\n\n';
    }
    
    const migration_path = path.join(migrations_dir, '001_create_tables.sql');
    fs.writeFileSync(migration_path, sql);
    print("Generated: " + migration_path);
    print("Dialect: " + dialect);
    print("Found " + entities.length + " @entity struct(s), ordered by dependencies");
}

// Generate self-extracting .run package
function generate_package() {
    const config_path = path.join(process.cwd(), 'omni.config.json');
    if (!fs.existsSync(config_path)) {
        print("Error: omni.config.json not found");
        return;
    }
    
    const config = JSON.parse(fs.readFileSync(config_path, 'utf-8'));
    const project_name = config.project?.name || 'app';
    
    // Build first
    build_from_config();
    
    // Collect all JS files from dist
    const js_dist = path.join(process.cwd(), 'dist', 'js');
    let bundled_code = "";
    
    if (fs.existsSync(js_dist)) {
        const files = fs.readdirSync(js_dist).filter(f => f.endsWith('.js'));
        for (const file of files) {
            bundled_code += "// === " + file + " ===\n";
            bundled_code += fs.readFileSync(path.join(js_dist, file), 'utf-8');
            bundled_code += "\n\n";
        }
    }
    
    // Include migrations
    const migrations_path = path.join(process.cwd(), 'dist', 'migrations', '001_create_tables.sql');
    let migrations_sql = "";
    if (fs.existsSync(migrations_path)) {
        migrations_sql = fs.readFileSync(migrations_path, 'utf-8');
    }
    
    // Create self-extracting shell script
    const payload_b64 = Buffer.from(bundled_code).toString('base64');
    const sql_b64 = Buffer.from(migrations_sql).toString('base64');
    
    const run_script = `#!/bin/bash
# ================================================================
# ${project_name}.run - Self-Extracting Omni Package
# Generated by Omni Compiler
# ================================================================

set -e

# Check for Node.js
if ! command -v node &> /dev/null; then
    echo "[omni] Node.js is required but not installed."
    echo "[omni] Install: https://nodejs.org/"
    exit 1
fi

# Create temp directory
TMPDIR=$(mktemp -d)
trap "rm -rf $TMPDIR" EXIT

# Extract embedded code
echo "[omni] Extracting ${project_name}..."
echo '${payload_b64}' | base64 -d > "$TMPDIR/app.js"

# Extract migrations
echo '${sql_b64}' | base64 -d > "$TMPDIR/migrations.sql"

# Show migrations
echo "[omni] Migrations:"
cat "$TMPDIR/migrations.sql"

# Run
echo "[omni] Starting ${project_name}..."
cd "$TMPDIR"
node app.js "$@"
`;

    // Windows batch version
    const bat_script = `@echo off
REM ================================================================
REM ${project_name}.run - Self-Extracting Omni Package
REM Generated by Omni Compiler
REM ================================================================

where node >nul 2>nul
if %ERRORLEVEL% neq 0 (
    echo [omni] Node.js is required but not installed.
    echo [omni] Install: https://nodejs.org/
    exit /b 1
)

echo [omni] Starting ${project_name}...
powershell -Command "[System.Text.Encoding]::UTF8.GetString([Convert]::FromBase64String('${payload_b64}'))" > %TEMP%\\omni_app.js
node %TEMP%\\omni_app.js %*
del %TEMP%\\omni_app.js
`;

    // Write both formats
    const run_path = path.join(process.cwd(), 'dist', project_name + '.run');
    const bat_path = path.join(process.cwd(), 'dist', project_name + '.bat');
    
    fs.writeFileSync(run_path, run_script, { mode: 0o755 });
    fs.writeFileSync(bat_path, bat_script);
    
    print("Generated: " + run_path + " (Unix)");
    print("Generated: " + bat_path + " (Windows)");
    print("Run with: ./" + project_name + ".run or " + project_name + ".bat");
}

function main() {
    let args_len = process.argv.length;
    
    // Check for 'build' command
    if (args_len >= 3 && process.argv[2] === 'build') {
        build_from_config();
        generate_dockerfiles();
        return;
    }
    
    // Check for 'package' command
    if (args_len >= 3 && process.argv[2] === 'package') {
        generate_package();
        return;
    }
    
    // Check for 'report' command
    if (args_len >= 3 && process.argv[2] === 'report') {
        generate_report();
        return;
    }
    
    // Check for 'docker' command
    if (args_len >= 3 && process.argv[2] === 'docker') {
        generate_dockerfiles();
        return;
    }
    
    // Check for 'migrate' command
    if (args_len >= 3 && process.argv[2] === 'migrate') {
        generate_migrations();
        return;
    }
    
    // Check for 'compile' command with --target flag (Hybrid Metamorphosis)
    if (args_len >= 3 && process.argv[2] === 'compile') {
        let targetLang = 'js';
        let inputFile = null;
        let outputFile = null;
        let framework = null;
        
        for (let i = 3; i < args_len; i++) {
            const arg = process.argv[i];
            if (arg === '--target' && i + 1 < args_len) {
                targetLang = process.argv[++i];
            } else if (arg === '--framework' && i + 1 < args_len) {
                framework = process.argv[++i];
            } else if (arg === '-o' && i + 1 < args_len) {
                outputFile = process.argv[++i];
            } else if (!arg.startsWith('-')) {
                inputFile = arg;
            }
        }
        
        if (!inputFile) {
            print("Usage: omni compile <input.omni> --target <lang> [-o output]");
            print("  --target  Target language (js, python, lua, c)");
            print("  --framework  Framework adapter (nextjs, laravel, fastapi)");
            return;
        }
        
        // Use Hybrid CodeGenerator
        if (HybridCodeGenerator) {
            print("[hybrid] Compiling: " + inputFile + " -> " + targetLang);
            
            const source = read_file(inputFile);
            const l = new_lexer(source);
            const p = new_parser(l);
            const program = Parser_parse_program(p);
            
            const gen = new HybridCodeGenerator({
                target: targetLang,
                framework: framework
            });
            
            const code = gen.generate(program);
            
            // Determine output file
            const extMap = { js: '.js', python: '.py', lua: '.lua', c: '.c' };
            const ext = extMap[targetLang] || '.txt';
            outputFile = outputFile || path.basename(inputFile, '.omni') + ext;
            
            write_file(outputFile, code);
            print("[hybrid] Generated: " + outputFile);
            print("[hybrid] Target: " + targetLang + (framework ? " + " + framework : ""));
        } else {
            print("Error: HybridCodeGenerator not available");
        }
        return;
    }
    
    if (args_len < 4) {
        print("Usage:");
        print("  node main.js <input_file> <output_file>  - Compile single file");
        print("  node main.js build                        - Build from omni.config.json");
        print("  node main.js report                       - Generate HTML report from flight_recorder.json");
        print("  node main.js docker                       - Generate Dockerfiles for targets");
        print("  node main.js migrate                      - Generate SQL migrations from @entity structs");
        return;
    }

    let input_path = process.argv[2];
    let output_path = process.argv[3];
    let base_dir = path.dirname(input_path);
    
    compile_file(input_path, output_path, 'js', base_dir);
}


// Entry Point

Object.assign(global, token);
Object.assign(global, lexer);
Object.assign(global, parser);
Object.assign(global, codegen);
Object.assign(global, io);

if (require.main === module) {
    main();
}
