// Generate Dockerfiles for targets
function generate_dockerfiles() {
    const config_path = path.join(process.cwd(), 'omni.config.json');
    if (!fs.existsSync(config_path)) {
        print("Error: omni.config.json not found");
        return;
    }
    
    const config = JSON.parse(fs.readFileSync(config_path, 'utf-8'));
    
    if (!config.docker) {
        print("No 'docker' section in omni.config.json");
        return;
    }
    
    for (const [target_name, docker_config] of Object.entries(config.docker)) {
        const target = config.targets?.[target_name];
        if (!target) continue;
        
        const is_python = target.format === 'python';
        const output_dir = target.output;
        const port = docker_config.port || 3000;
        const base = docker_config.base || (is_python ? 'python:3.11-slim' : 'node:18-alpine');
        
        let dockerfile;
        if (is_python) {
            dockerfile = `# Generated by Omni Compiler
FROM ${base}
WORKDIR /app
COPY requirements.txt ./
RUN pip install --no-cache-dir -r requirements.txt || true
COPY ${output_dir}/ ./
EXPOSE ${port}
CMD ["python", "server.py"]
`;
        } else {
            dockerfile = `# Generated by Omni Compiler
FROM ${base}
WORKDIR /app
COPY package*.json ./
RUN npm install --production || true
COPY ${output_dir}/ ./
EXPOSE ${port}
CMD ["node", "server.js"]
`;
        }
        
        const dockerfile_path = path.join(output_dir, 'Dockerfile');
        if (!fs.existsSync(output_dir)) {
            fs.mkdirSync(output_dir, { recursive: true });
        }
        fs.writeFileSync(dockerfile_path, dockerfile);
        print("Generated: " + dockerfile_path);
    }
    
    print("Dockerfiles generated!");
}

// Generate SQL migrations from @entity structs
function generate_migrations() {
    const config_path = path.join(process.cwd(), 'omni.config.json');
    if (!fs.existsSync(config_path)) {
        print("Error: omni.config.json not found");
        return;
    }
    
    const config = JSON.parse(fs.readFileSync(config_path, 'utf-8'));
    const shared_dirs = config.shared || [];
    
    // Type mapping for SQL
    const typeMap = {
        'i64': 'INTEGER',
        'i32': 'INTEGER',
        'f64': 'REAL',
        'f32': 'REAL',
        'string': 'TEXT',
        'bool': 'INTEGER'
    };
    
    let migrations = [];
    
    // Scan all shared directories for @entity structs
    for (const dir of shared_dirs) {
        const full_dir = path.join(process.cwd(), dir);
        if (!fs.existsSync(full_dir)) continue;
        
        const files = fs.readdirSync(full_dir).filter(f => f.endsWith('.omni'));
        for (const file of files) {
            const content = fs.readFileSync(path.join(full_dir, file), 'utf-8');
            
            // Parse to find @entity structs
            const l = new_lexer(content);
            const p = new_parser(l);
            const program = Parser_parse_program(p);
            
            for (const stmt of program.statements) {
                if (stmt.kind === NODE_STRUCT && stmt.attributes) {
                    for (const attr of stmt.attributes) {
                        if (attr.name === 'entity') {
                            const storage = attr.params.storage || 'main_db';
                            const table = stmt.name;
                            
                            let columns = [];
                            for (const field of stmt.fields) {
                                const sqlType = typeMap[field.typename] || 'TEXT';
                                const pk = field.name === 'id' ? ' PRIMARY KEY AUTOINCREMENT' : '';
                                columns.push('    ' + field.name + ' ' + sqlType + pk);
                            }
                            
                            migrations.push({
                                storage,
                                table,
                                sql: 'CREATE TABLE IF NOT EXISTS ' + table + ' (\\n' + columns.join(',\\n') + '\\n);'
                            });
                        }
                    }
                }
            }
        }
    }
    
    if (migrations.length === 0) {
        print("No @entity structs found in shared directories.");
        return;
    }
    
    // Generate migration file
    const migrations_dir = path.join(process.cwd(), 'dist', 'migrations');
    if (!fs.existsSync(migrations_dir)) {
        fs.mkdirSync(migrations_dir, { recursive: true });
    }
    
    let sql_content = '-- Generated by Omni Compiler\\n';
    sql_content += '-- Run this SQL to create tables for @entity structs\\n\\n';
    
    for (const m of migrations) {
        sql_content += '-- Table: ' + m.table + ' (storage: ' + m.storage + ')\\n';
        sql_content += m.sql + '\\n\\n';
    }
    
    const migration_path = path.join(migrations_dir, '001_create_tables.sql');
    fs.writeFileSync(migration_path, sql_content.replace(/\\\\n/g, '\\n'));
    print("Generated: " + migration_path);
    print("Found " + migrations.length + " @entity struct(s)");
}

function main() {
    let args_len = process.argv.length;
    
    // Check for 'build' command
    if (args_len >= 3 && process.argv[2] === 'build') {
        build_from_config();
        generate_dockerfiles();
        return;
    }
    
    // Check for 'report' command
    if (args_len >= 3 && process.argv[2] === 'report') {
        generate_report();
        return;
    }
    
    // Check for 'docker' command
    if (args_len >= 3 && process.argv[2] === 'docker') {
        generate_dockerfiles();
        return;
    }
    
    // Check for 'migrate' command
    if (args_len >= 3 && process.argv[2] === 'migrate') {
        generate_migrations();
        return;
    }
    
    if (args_len < 4) {
        print("Usage:");
        print("  node main.js <input_file> <output_file>  - Compile single file");
        print("  node main.js build                        - Build from omni.config.json");
        print("  node main.js report                       - Generate HTML report from flight_recorder.json");
        print("  node main.js docker                       - Generate Dockerfiles for targets");
        print("  node main.js migrate                      - Generate SQL migrations from @entity structs");
        return;
    }

    let input_path = process.argv[2];
    let output_path = process.argv[3];
    let base_dir = path.dirname(input_path);
    
    compile_file(input_path, output_path, 'js', base_dir);
}


// Entry Point

Object.assign(global, token);
Object.assign(global, lexer);
Object.assign(global, parser);
Object.assign(global, codegen);
Object.assign(global, io);

if (require.main === module) {
    main();
}
